import React, {useState, useEffect} from "react";
import './App.css';
import Cover from "./components/cover/Cover";
import Navbar from "./components/navbar/Navbar";
import About from "./components/about/About";

function App() {
  const [scrollHeight, setScrollHeight] = useState(0); // Scroll de la pagina
  const handleScroll = () => {
    const position = window.pageYOffset;
    setScrollHeight(position);
  }

  //Actualizo posicion de scroll

  useEffect(() => {window.addEventListener("scroll", handleScroll)}, [scrollHeight]);

  return (
    //Le paso la propiedad de la posicion del scroll
    <div className="App">
      <Navbar isScrolling={scrollHeight} /> 
      <Cover />
      <About />
    </div>
  );
}

export default App;
